sections.css:
.t-h1 {
    margin: 0;
    padding: 0;

    font-family: font(main);

    color: color(white);
    font-weight: $font-weight-black;

    font-size: font-size(h1-m);
    line-height: font-size(h1-m) * 1.2;

    @include media('>=mobile') {
        font-size: font-size(h1);
        line-height: font-size(h1) * 1.18;
    }
}

.t-h2 {
    margin: 0;
    padding: 0;

    font-family: font(main);

    color: color(main);
    font-weight: $font-weight-black;

    font-size: font-size(h2-m);
    line-height: font-size(h2-m) * 1.13;

    @include media('>=mobile') {
        font-size: font-size(h2);
        line-height: font-size(h2) * 1.22;
    }
}

.t-h3 {
    margin: 0;
    padding: 0;

    font-family: font(main);

    color: color(main);
    font-weight: $font-weight-bold;

    font-size: font-size(body-m);
    line-height: font-size(body-m) * 1.5;

    @include media('>=mobile') {
        font-size: font-size(body);
        line-height: font-size(body) * 1.9;
    }
}

.t-h4 {
    margin: 0;
    padding: 0;

    font-family: font(main);

    color: color(main);
    font-weight: $font-weight-bold;
    text-align: center;

    font-size: font-size(body-m);
    line-height: font-size(body-m) * 1.5;

    @include media('>=mobile') {
        font-size: font-size(body);
        line-height: font-size(body) * 1.9;
    }
}

.t-h5 {
    margin: 0;
    padding: 0;

    font-family: font(main);

    color: color(main);
    font-weight: $font-weight-black;

    font-size: font-size(body-m);
    line-height: font-size(body-m) * 1.5;

    @include media('>=mobile') {
        font-size: font-size(body);
        line-height: font-size(body) * 1.9;
    }
}

.t-body {
    margin: 0;
    padding: 0;

    font-family: font(main);

    color: color(main);

    font-size: font-size(body-m);
    line-height: font-size(body-m) * 1.5;
    font-weight: $font-weight-regular;

    margin-bottom: offset(main) * 1.4;

    max-width: 100%;

    a {
        font-weight: $font-weight-bold;
    }

    @include media('>=mobile') {
        font-size: font-size(body);
        line-height: font-size(body) * 1.56;
    }

    &--big {
        font-size: font-size(body--big);
        line-height: font-size(body--big) * 1.4;
    }

    &--alt {
        @include media('>=mobile') {
            font-size: font-size(body-m);
            line-height: font-size(body-m) * 1.5;
        }
    }

    &--padded {
        line-height: font-size(body-m) * 1.75;
    }

    &--alt-mobile {
        font-size: font-size(small-m);
        line-height: font-size(small-m) * 1.5;

        @include media('>=mobile') {
            font-size: font-size(body-m);
            line-height: font-size(body-m) * 1.5;
        }
    }
}

.t-small {
    margin: 0;
    padding: 0;

    font-family: font(main);

    color: color(dgrey);

    margin-bottom: offset(main);

    font-size: font-size(small-m);
    line-height: font-size(small-m) * 1.4;

    @include media('>=mobile') {
        font-size: font-size(small);
        line-height: font-size(small) * 1.4;
    }
}

.t-smaller {
    margin: 0;
    padding: 0;

    font-family: font(main);

    color: color(dgrey);

    margin-bottom: offset(main);

    font-size: font-size(smaller);
    line-height: font-size(smaller) * 1.4;
}

.t-btn {
    margin: 0;
    padding: 0;

    font-family: font(main);

    color: color(dgrey);
    font-weight: $font-weight-bold;
    letter-spacing: 0.5;

    font-size: font-size(small-m);
    line-height: font-size(small-m) * 1.11;

    @include media('>=mobile') {
        font-size: font-size(small);
        line-height: font-size(small) * 1.11;
    }

    &--small {
        font-size: font-size(smallest);
        line-height: font-size(smallest) * 1.4;

        @include media('>=mobile') {
            font-size: font-size(smallest);
            line-height: font-size(smallest) * 1.4;
        }
    }
}

.t-sec-divider {
    margin: 0;
    padding: 0;

    font-family: font(main);

    color: color(main);
    letter-spacing: offset(main) / 2;
    text-transform: uppercase;
    text-align: center;

    font-size: font-size(body);
    line-height: font-size(body) * 1.22;
}

.t-standfirst {
    margin: 0;
    padding: 0;

    font-family: font(main);

    color: color(main);
    text-align: center;

    font-size: font-size(standfirst-m);
    line-height: font-size(standfirst-m) * 1.55;

    @include media('>=mobile') {
        font-size: font-size(standfirst);
        line-height: font-size(standfirst) * 1.45;
    }
}

.t-link {
    margin: 0;
    padding: 0;

    color: color(main);

    position: relative;

    cursor: pointer;

    margin-bottom: offset(main) * 1.4;

    .c-label {
        font-family: font(main);

        font-weight: $font-weight-bold;

        font-size: font-size(small);
        line-height: font-size(small);

        &:after {
            position: absolute;
            content: '';

            left: 0;
            bottom: - offset(main) / 3;

            width: 100%;
            height: 1px;

            background-color: color(accent);

            transition: $transition-default;
        }
    }

    &:hover {
        .c-label {
            &:after {
                background-color: color(main);
            }
        }
    }
}

.t-underline {
    margin: 0;
    padding: 0;

    position: relative;

    &:after {
        position: absolute;
        content: '';

        left: 0;
        bottom: - offset(main) / 3;

        width: 100%;
        height: 1px;

        background-color: color(accent);

        transition: $transition-default;
    }

    &:hover {
        &:after {
            background-color: color(main);
        }
    }
}

.t-body-link {
    margin: 0;
    padding: 0;

    position: relative;

    margin-bottom: offset(main) * 1.4;

    .c-label {
        font-family: font(main);

        color: color(main);
        font-weight: $font-weight-bold;

        font-size: font-size(body-m);
        line-height: font-size(body-m) * 1.5;

        &:after {
            position: absolute;
            content: '';

            left: 0;
            bottom: - offset(main) / 3;

            width: 100%;
            height: 1px;

            background-color: color(accent);

            transition: $transition-default;
        }

        @include media('>=mobile') {
            font-size: font-size(body);
            line-height: font-size(body) * 1.9;
        }
    }

    &:hover {
        .c-label {
            &:after {
                background-color: color(main);
            }
        }
    }
}

.t-timeline-number {
    margin: 0;
    padding: 0;

    font-family: font(main);
    font-weight: $font-weight-light;

    color: color(main);

    font-size: font-size(timeline);
    line-height: font-size(timeline) * 1.22;
}

.t-big-number {
    margin: 0;
    padding: 0;

    font-family: font(main);
    font-weight: $font-weight-light;
    text-align: center;

    color: color(main);

    font-size: font-size(big);
    line-height: font-size(big);
}

.t-big-info {
    margin: 0;
    padding: 0;

    font-family: font(main);
    font-weight: $font-weight-regular;
    text-align: center;

    color: color(main);

    font-size: font-size(info);
    line-height: font-size(info) * 1.2;
}

.t-menu {
    margin: 0;
    padding: 0;

    font-family: font(main);

    color: color(main);

    font-size: font-size(small);
    line-height: font-size(small) * 1.28;
}

.t-menu--small {
    font-size: font-size(smaller);
}

.t-menu-sub {
    margin: 0;
    padding: 0;

    font-family: font(main);

    color: color(main);

    font-size: font-size(small);
    line-height: font-size(small);
}

/* Text utils
========================================================================== */

.t-thin {
    font-weight: $font-weight-regular;
}

.t-center {
    text-align: center;
}

.t-white {
    color: color(white);
}

.t-blue {
    color: color(main);
}

.t-dgrey {
    color: color(dgrey);
}

.t-bold {
    font-weight: $font-weight-bold;
}

/* Global texts
========================================================================== */
html {
    @include font-smoothing(on);

    font-size: font-size(body);
    font-family: font(main);

    font-weight: $font-weight-regular;
}

body {
    font-family: font(main);
    color: color(main);

    font-weight: $font-weight-regular;

    line-height: default(line-height);
}

a {
    cursor: pointer;
    text-decoration: none;

    color: currentColor;
    &:hover {
        text-decoration: none;
    }
}

.l-header {
    width: 100%;
    height: $size-header-height-m;

    z-index: $z-index-header;

    @include media('>=mobile') {
        height: $size-header-height-d;
    }
}

.c-article-header__date {
    margin-top: offset(main) * 3.5;

    @include media('>=mobile') {
        margin-top: offset(main);
    }
}

.c-article-header-wrapper__title {
    margin-bottom: offset(main) * 3;

    @include media('>=mobile') {
        margin-bottom: offset(main) * 7.5;
    }
}

.l-footer {
    width: 100%;
    height: $size-footer-height;

    background-color: color(main);
}

.l-footer .l-inner {
    height: 100%;
}

.is-header-open .l-footer {
    position: fixed;
    bottom: 0;
    left: 0;

    transition: $transition-default;

    z-index: $z-index-header - 1;
}

.is-footer-fixed .l-footer {
    position: fixed;
    bottom: 0;
    left: 0;

    transition: $transition-default;

    z-index: $z-index-header - 1;
}

.c-header-panel {
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: flex-start;

    padding-top: offset(main) * 3;

    z-index: 10;

    @include media ('>=mobile-small') {
        padding-top: offset(main) * 8.5;
    }
    @include media('>=mobile') {
        padding-top: offset(main) * 18;
    }
    @include media('>tablet') {
        padding-top: offset(main) * 18;
    }
}

.c-header-panel__desc {
    padding-bottom: offset(main) * 4.5;
    margin-bottom: 0;

    @include media('>=mobile') {
        padding-bottom: offset(main) * 8.5;
        padding-top: offset(main);
    }
}

.c-header-panel__arrow-container {
    display: block;
    padding: offset(main);

    padding-bottom: offset(main) * 8;

    cursor: pointer;

    @include media('>=mobile') {
        padding-bottom: offset(main) * 10.5;
    }
}

.c-header-panel__arrow {
    @include arrow;
    transform: rotate(45deg);
}

/* ==========================================================================
Header panel short
========================================================================== */

.c-header-panel-tab {
    list-style: none;

    width: $size-header-panel-tab-width;
    height: $size-header-panel-tab-height;

    margin: 0 auto;

    background-color: color(lblue);

    position: relative;

    @include media('>=mobile') {
        margin: 0;

        width: $size-header-panel-tab-width-t;
        height: $size-header-panel-tab-height-t;
    }
}

.c-header-panel-tab:nth-child(2) {
    @include media('>=mobile') {
        margin-left: offset(main) * 3;
    }
}

.c-header-panel-tab:hover {
    background-color: color(accent);
}

.c-header-panel-tab:before,
.c-header-panel-tab:after {
    content: '';
    display: block;

    width: offset(main) * 2;
    height: $size-header-panel-tab-height;

    position: absolute;
    top: 0;

    background-color: color(lblue);

    transform: skewX(-12deg);
}

.c-header-panel-tab:hover:before,
.c-header-panel-tab:hover:after {
    background-color: color(accent);
}

.c-header-panel-tab:before {
    left: - offset(main);
}

.c-header-panel-tab:after {
    right: - offset(main);
    border-right: $size-big-border solid color(accent);
}

.c-header-panel-tab:nth-child(2) {
    display: none;

    @include media('>=mobile') {
        display: list-item;
    }
}

.c-header-panel-tab > .c-link {
    width: 100%;
    height: 100%;

    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
}

.c-header-panel-tab__title {
    margin: 0;
}

.c-header-panel-tab:hover .c-header-panel-tab__title {
    color: color(white);
}

.c-header-panel-tab__subtitle {
    margin: 0;
}

.c-header-panel-tab:hover .c-header-panel-tab__subtitle {
    color: color(white);
}

.c-header-panel-tab__hover {
    position: absolute;
    top: 0;
    left: 0;

    width: 90%;
    height: $size-header-panel-tab-height;

    background-color: color(accent);

    opacity: 0;

    display: flex;
    flex-direction: row;
    justify-content: space-between;
    align-items: center;
}

.c-header-panel-tab:hover .c-header-panel-tab__hover {
    opacity: 1;
}

.c-header-panel-tab__hover-text {
    padding: 0;
    margin: 0;
    max-width: 50%;
    font-weight: bold;
}

.c-header-panel-tab__hover-icon {
    width: 25px;
    height: 17px;

    margin-right: - offset(main) * 0.8;

    color: color(white);
}

.c-header-panel-tab__hover-icon .o-svg {
    width: 100%;
    height: 100%;

    fill: currentColor;
}

.c-header-panel-tab--alt .c-header-panel-tab__hover {
    left: 50%;
    transform: translateX(-50%);

    width: 80%;
}

.c-header-panel-tabs {
    width: 100%;

    list-style: none;

    padding: 0;
    margin: 0;

    z-index: 10;

    left: 0;

    position: absolute;
    bottom: - $size-header-panel-tab-height / 2;

    @include media('>=mobile') {
        display: flex;
        flex-direction: row;
        align-items: center;
        justify-content: center;
    }
}

.c-header-panel--short {
    padding-bottom: offset(main) * 8.5;
    height: $size-header-panel-short-height;

    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;

    @include media('>=mobile') {
        padding-top: offset(main) * 14;
    }
}

.c-header-panel--short .c-header-panel__desc {
    padding-bottom: 0;

    @include media('>=mobile') {
        max-width: 78%;
    }
}

.c-latest-news-wrapper {
    width: 100%;
    height: auto;

    background-size: cover;
    background-position: 50% 50%;

    padding-top: offset(main) * 7;
    padding-bottom: offset(main) * 4;

    @include media('>=mobile') {
        width: 100%;
    }
}

.c-latest-news-wrapper--padded {
    padding-bottom: offset(main) * 10;
}

.c-latest-news-wrapper__slider {
    display: flex;
    flex-direction: row;
    align-items: center;
    justify-content: center;

    width: 100%;
    height: auto;
    padding-bottom: offset(main) * 10.5;

    @include media('>=mobile') {
        padding-left: offset(main) * 3.2;
        padding-right: offset(main) * 3.2;
    }
    @include media('>=desktop-small') {
        padding-left: 0;
        padding-right: 0;
    }
}

.c-latest-news-wrapper__next,
.c-latest-news-wrapper__prev {
    position: absolute;

    @include arrow-small;
    cursor: pointer;

    z-index: $size-latest-news-arrow-z-index;

    outline: none;

    @include media('>=desktop-small') {
        display: none;
    }
}

.c-latest-news-wrapper__prev {
    transform: rotate(135deg);
    left: offset(main) * 0.5;
}

.c-latest-news-wrapper__next {
    transform: rotate(-45deg);
    right: offset(main) * 0.5;
}

.c-section-divider {
    width: 100%;

    margin: 0;

    display: flex;
    align-items: center;
    justify-content: center;

    text-align: center;
}

.c-section-divider .l-inner {
    margin: 0;
}

.c-section-divider__title {
    padding-bottom: offset(main) * 3;
}

.c-section-divider--nopadding .c-section-divider__title {
    padding-bottom: 0;
}

.c-section-divider--padded .c-section-divider__title {
    padding-bottom: offset(main) * 5;
}

.c-btn-wrapper {
    width: 100%;

    display: flex;
    align-items: center;
    justify-content: center;
}

.c-btn-wrapper--small {
    max-width: offset(main) * 23;
}

.c-btn-wrapper--medium {
    max-width: offset(main) * 32;
}

.c-btn-wrapper--left {
    justify-content: flex-start;
}

.c-btn-wrapper--right {
    justify-content: flex-end;
}

.c-latest-news-wrapper .c-btn {
    width: $size-latest-news-card;
}

.c-btn {
    width: 100%;
    height: auto;

    cursor: pointer;

    padding: {
        top: offset(main) * 1.5;
        bottom: offset(main) * 1.5;
    }
    &--medium {
        width: $size-btn-medium-width;
    }
    &--center {
        text-align: center;
    }
    &--block {
        display: block;
        width: auto;
    }
}

.c-btn--primary {
    background-color: color(accent);

    padding: {
        left: offset(main) * 1.5;
        right: offset(main) * 1.5;
    }

    position: relative;
}

@include media('>=desktop-small') {

    .c-btn--primary:hover {
        text-align: left;

        .c-label {

            &:after {
                content: '';

                display: block;

                width: offset(main) * 2;
                height: offset(main) * 1.5;

                position: absolute;
                top: offset(main) * 1.5;
                right: offset(main) * 1.5;

                background-image: url('./../img/icon-arrow.svg');
                background-size: cover;
                background-position: 50% 50%;
                background-repeat: no-repeat;
            }
        }
    }
}

.c-btn--download {
    display: flex;
    flex-direction: column;
    flex-wrap: wrap;
    align-items: center;

    background-color: color(accent);

    position: relative;

    padding: {
        top: offset(main);
        bottom: offset(main) * 0.6;
    }
}

.c-btn--download .c-label {
    font-weight: $font-weight-bold;
}

.c-btn--download .c-sub-label {
    font-weight: $font-weight-regular;
}

.c-btn--download .c-btn__icon {
    right: offset(main) * 2;
    opacity: 0;
}

.c-btn--download .o-svg {
    stroke: color(white);
}

@include media('>=desktop-small') {

    .c-btn--download:hover {
        align-items: flex-start;

        .c-label,
        .c-sub-label {
            margin-left: offset(main) * 1.5;
        }

        .c-btn__icon {
            opacity: 1;
        }
    }

    .c-btn--icon-hidden:hover {
        .c-btn__icon {
            opacity: 0;
        }
    }

}

.c-btn--icon-centered {
    &:hover {
        .c-label,
        .c-sub-label {
            opacity: 0;
        }

        .c-btn__icon {
            opacity: 1;
            right: 0;
            left: 0;
            margin: auto;
        }
    }
}

.c-btn--download-icon {
    display: flex;
    position: relative;
    width: 100%;
    padding: 0;

    @include media('>=mobile') {
        width: auto;
        flex-direction: column;
        background-color: transparent;
        align-items: flex-start;
        border-left: 2px solid color(lgrey);

        padding: {
            top: offset(main);
            bottom: offset(main) * 0.6;
            left: offset(main) * 3;
            right: offset(main) * 6;
        }
    }
}

.c-btn--download-icon .c-sub-label {
    margin-left: offset(main) * 2;
    font-weight: $font-weight-regular;

    @include media('>=mobile') {
        margin-left: 0;
    }
}

.c-btn--download-icon .o-svg {
    stroke: color(accent);
}

.c-btn__icon {
    width: offset(main) * 3.6;
    height: offset(main) * 2.6;

    position: absolute;
    top: 0;
    bottom: offset(main);
    right: 0;
    margin: auto 0;

    @include media('>=mobile') {
        bottom: 0;
    }
}

.c-btn__icon .o-svg {
    width: 100%;
    height: 100%;
}

.c-btn--padded {
    padding: {
        left: offset(main) * 6;
        right: offset(main) * 6
    }
}

.c-btn-wrapper--equal-btns {
    justify-content: space-between;
    flex-wrap: wrap;
}

.c-btn-wrapper--equal-btns .c-btn {
    width: 100%;
    margin-bottom: offset(main);

    &:last-child {
        margin-bottom: 0;
    }

    @include media('>=mobile') {
        margin-bottom: 0;
    }
}

.c-btn-wrapper--two-btns .c-btn {
    @include media('>=mobile') {
        width: calc(50% - #{offset(main) * 1.2});
    }
}

.c-btn-wrapper--three-btns .c-btn {
    @include media('>=mobile') {
        width: calc(33% - #{offset(main) * 1.2});
    }
}

.c-feature-panel {
    width: 100%;

    @include media('>=mobile') {
        width: 50%;
        height: 100%;

        display: flex;
        flex-direction: column;
        justify-content: flex-start;
    }
    @include media('>=desktop-small') {
        width: 40%;
    }
}

.c-feature-panel__title,
.c-feature-panel__content {
    margin: 0;
}

.c-feature-panel__title {
    padding-top: offset(main) * 3;
    padding-bottom: offset(main) * 2;

    @include media('>=mobile') {
        padding-top: 0;
        padding-bottom: offset(main) * 3.5;
    }
}

.c-feature-panel .c-btn-wrapper {
    margin-top: auto;
    padding-top: offset(main) * 7;

    @include media('>=mobile') {
        padding-top: 0;
    }
}

.c-quick-links-wrapper {
    width: 100%;
    height: auto;

    background-size: cover;
    background-position: 50% 50%;
}

.c-quick-links-wrapper__slider {
    display: flex;
    flex-direction: row;
    align-items: center;
    justify-content: center;

    width: 100%;
