}

.t-menu--small {
    font-size: font-size(smaller);
}

.t-menu-sub {
    margin: 0;
    padding: 0;

    font-family: font(main);

    color: color(main);

    font-size: font-size(small);
    line-height: font-size(small);
}

/* Text utils
========================================================================== */

.t-thin {
    font-weight: $font-weight-regular;
}

.t-center {
    text-align: center;
}

.t-white {
    color: color(white);
}

.t-blue {
    color: color(main);
}

.t-dgrey {
    color: color(dgrey);
}

.t-bold {
    font-weight: $font-weight-bold;
}

/* Global texts
========================================================================== */
html {
    @include font-smoothing(on);

    font-size: font-size(body);
    font-family: font(main);

    font-weight: $font-weight-regular;
}

body {
    font-family: font(main);
    color: color(main);

    font-weight: $font-weight-regular;

    line-height: default(line-height);
}

a {
    cursor: pointer;
    text-decoration: none;

    color: currentColor;
    &:hover {
        text-decoration: none;
    }
}

.l-header {
    width: 100%;
    height: $size-header-height-m;

    z-index: $z-index-header;

    @include media('>=mobile') {
        height: $size-header-height-d;
    }
}

.c-article-header__date {
    margin-top: offset(main) * 3.5;

    @include media('>=mobile') {
        margin-top: offset(main);
    }
}

.c-article-header-wrapper__title {
    margin-bottom: offset(main) * 3;

    @include media('>=mobile') {
        margin-bottom: offset(main) * 7.5;
    }
}

.l-footer {
    width: 100%;
    height: $size-footer-height;

    background-color: color(main);
}

.l-footer .l-inner {
    height: 100%;
}

.is-header-open .l-footer {
    position: fixed;
    bottom: 0;
    left: 0;

    transition: $transition-default;

    z-index: $z-index-header - 1;
}

.is-footer-fixed .l-footer {
    position: fixed;
    bottom: 0;
    left: 0;

    transition: $transition-default;

    z-index: $z-index-header - 1;
}

.c-header-panel {
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: flex-start;

    padding-top: offset(main) * 3;

    z-index: 10;

    @include media ('>=mobile-small') {
        padding-top: offset(main) * 8.5;
    }
    @include media('>=mobile') {
        padding-top: offset(main) * 18;
    }
    @include media('>tablet') {
        padding-top: offset(main) * 18;
    }
}

.c-header-panel__desc {
    padding-bottom: offset(main) * 4.5;
    margin-bottom: 0;

    @include media('>=mobile') {
        padding-bottom: offset(main) * 8.5;
        padding-top: offset(main);
    }
}

.c-header-panel__arrow-container {
    display: block;
    padding: offset(main);

    padding-bottom: offset(main) * 8;

    cursor: pointer;

    @include media('>=mobile') {
        padding-bottom: offset(main) * 10.5;
    }
}

.c-header-panel__arrow {
    @include arrow;
    transform: rotate(45deg);
}

/* ==========================================================================
Header panel short
========================================================================== */

.c-header-panel-tab {
    list-style: none;

    width: $size-header-panel-tab-width;
    height: $size-header-panel-tab-height;

    margin: 0 auto;

    background-color: color(lblue);

    position: relative;

    @include media('>=mobile') {
        margin: 0;

        width: $size-header-panel-tab-width-t;
        height: $size-header-panel-tab-height-t;
    }
}

.c-header-panel-tab:nth-child(2) {
    @include media('>=mobile') {
        margin-left: offset(main) * 3;
    }
}

.c-header-panel-tab:hover {
    background-color: color(accent);
}

.c-header-panel-tab:before,
.c-header-panel-tab:after {
    content: '';
    display: block;

    width: offset(main) * 2;
    height: $size-header-panel-tab-height;

    position: absolute;
    top: 0;

    background-color: color(lblue);

    transform: skewX(-12deg);
}

.c-header-panel-tab:hover:before,
.c-header-panel-tab:hover:after {
    background-color: color(accent);
}

.c-header-panel-tab:before {
    left: - offset(main);
}

.c-header-panel-tab:after {
    right: - offset(main);
    border-right: $size-big-border solid color(accent);
}

.c-header-panel-tab:nth-child(2) {
    display: none;

    @include media('>=mobile') {
        display: list-item;
    }
}

.c-header-panel-tab > .c-link {
    width: 100%;
    height: 100%;

    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
}

.c-header-panel-tab__title {
    margin: 0;
}

.c-header-panel-tab:hover .c-header-panel-tab__title {
    color: color(white);
}

.c-header-panel-tab__subtitle {
    margin: 0;
}

.c-header-panel-tab:hover .c-header-panel-tab__subtitle {
    color: color(white);
}

.c-header-panel-tab__hover {
    position: absolute;
    top: 0;
    left: 0;

    width: 90%;
    height: $size-header-panel-tab-height;

    background-color: color(accent);

    opacity: 0;

    display: flex;
    flex-direction: row;
    justify-content: space-between;
    align-items: center;
}

.c-header-panel-tab:hover .c-header-panel-tab__hover {
    opacity: 1;
}

.c-header-panel-tab__hover-text {
    padding: 0;
    margin: 0;
    max-width: 50%;
    font-weight: bold;
}

.c-header-panel-tab__hover-icon {
    width: 25px;
    height: 17px;

    margin-right: - offset(main) * 0.8;

    color: color(white);
}

.c-header-panel-tab__hover-icon .o-svg {
    width: 100%;
    height: 100%;

    fill: currentColor;
}

.c-header-panel-tab--alt .c-header-panel-tab__hover {
    left: 50%;
    transform: translateX(-50%);

    width: 80%;
}

.c-header-panel-tabs {
    width: 100%;

    list-style: none;

    padding: 0;
    margin: 0;

    z-index: 10;

    left: 0;

    position: absolute;
    bottom: - $size-header-panel-tab-height / 2;

    @include media('>=mobile') {
        display: flex;
        flex-direction: row;
        align-items: center;
        justify-content: center;
    }
}

.c-header-panel--short {
    padding-bottom: offset(main) * 8.5;
    height: $size-header-panel-short-height;

    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;

    @include media('>=mobile') {
        padding-top: offset(main) * 14;
    }
}

.c-header-panel--short .c-header-panel__desc {
    padding-bottom: 0;

    @include media('>=mobile') {
        max-width: 78%;
    }
}

.c-latest-news-wrapper {
    width: 100%;
    height: auto;

    background-size: cover;
    background-position: 50% 50%;

    padding-top: offset(main) * 7;
    padding-bottom: offset(main) * 4;

    @include media('>=mobile') {
        width: 100%;
    }
}

.c-latest-news-wrapper--padded {
    padding-bottom: offset(main) * 10;
}

.c-latest-news-wrapper__slider {
    display: flex;
    flex-direction: row;
    align-items: center;
    justify-content: center;

    width: 100%;
    height: auto;
    padding-bottom: offset(main) * 10.5;

    @include media('>=mobile') {
        padding-left: offset(main) * 3.2;
        padding-right: offset(main) * 3.2;
    }
    @include media('>=desktop-small') {
        padding-left: 0;
        padding-right: 0;
    }
}

.c-latest-news-wrapper__next,
.c-latest-news-wrapper__prev {
    position: absolute;

    @include arrow-small;
    cursor: pointer;

    z-index: $size-latest-news-arrow-z-index;

    outline: none;

    @include media('>=desktop-small') {
        display: none;
    }
}

.c-latest-news-wrapper__prev {
    transform: rotate(135deg);
    left: offset(main) * 0.5;
}

.c-latest-news-wrapper__next {
    transform: rotate(-45deg);
    right: offset(main) * 0.5;
}

.c-section-divider {
    width: 100%;

    margin: 0;

    display: flex;
    align-items: center;
    justify-content: center;

    text-align: center;
}

.c-section-divider .l-inner {
    margin: 0;
}

.c-section-divider__title {
    padding-bottom: offset(main) * 3;
}

.c-section-divider--nopadding .c-section-divider__title {
    padding-bottom: 0;
}

.c-section-divider--padded .c-section-divider__title {
    padding-bottom: offset(main) * 5;
}

.c-btn-wrapper {
    width: 100%;

    display: flex;
    align-items: center;
    justify-content: center;
}

.c-btn-wrapper--small {
    max-width: offset(main) * 23;
}

.c-btn-wrapper--medium {
    max-width: offset(main) * 32;
}

.c-btn-wrapper--left {
    justify-content: flex-start;
}

.c-btn-wrapper--right {
    justify-content: flex-end;
}

.c-latest-news-wrapper .c-btn {
    width: $size-latest-news-card;
}

.c-btn {
    width: 100%;
    height: auto;

    cursor: pointer;

    padding: {
        top: offset(main) * 1.5;
        bottom: offset(main) * 1.5;
    }
    &--medium {
        width: $size-btn-medium-width;
    }
    &--center {
        text-align: center;
    }
    &--block {
        display: block;
        width: auto;
    }
}

.c-btn--primary {
    background-color: color(accent);

    padding: {
        left: offset(main) * 1.5;
        right: offset(main) * 1.5;
    }

    position: relative;
}

@include media('>=desktop-small') {

    .c-btn--primary:hover {
        text-align: left;

        .c-label {

            &:after {
                content: '';

                display: block;

                width: offset(main) * 2;
                height: offset(main) * 1.5;

                position: absolute;
                top: offset(main) * 1.5;
                right: offset(main) * 1.5;

                background-image: url('./../img/icon-arrow.svg');
                background-size: cover;
                background-position: 50% 50%;
                background-repeat: no-repeat;
            }
        }
    }
}

.c-btn--download {
    display: flex;
    flex-direction: column;
    flex-wrap: wrap;
    align-items: center;

    background-color: color(accent);

    position: relative;

    padding: {
        top: offset(main);
        bottom: offset(main) * 0.6;
    }
}

.c-btn--download .c-label {
    font-weight: $font-weight-bold;
}

.c-btn--download .c-sub-label {
    font-weight: $font-weight-regular;
}

.c-btn--download .c-btn__icon {
    right: offset(main) * 2;
    opacity: 0;
}

.c-btn--download .o-svg {
    stroke: color(white);
}

@include media('>=desktop-small') {

    .c-btn--download:hover {
        align-items: flex-start;

        .c-label,
        .c-sub-label {
            margin-left: offset(main) * 1.5;
        }

        .c-btn__icon {
            opacity: 1;
        }
    }

    .c-btn--icon-hidden:hover {
        .c-btn__icon {
            opacity: 0;
        }
    }

}

.c-btn--icon-centered {
    &:hover {
        .c-label,
        .c-sub-label {
            opacity: 0;
        }

        .c-btn__icon {
            opacity: 1;
            right: 0;
            left: 0;
            margin: auto;
        }
    }
}

.c-btn--download-icon {
    display: flex;
    position: relative;
    width: 100%;
    padding: 0;

    @include media('>=mobile') {
        width: auto;
        flex-direction: column;
        background-color: transparent;
        align-items: flex-start;
        border-left: 2px solid color(lgrey);

        padding: {
            top: offset(main);
            bottom: offset(main) * 0.6;
            left: offset(main) * 3;
            right: offset(main) * 6;
        }
    }
}

.c-btn--download-icon .c-sub-label {
    margin-left: offset(main) * 2;
    font-weight: $font-weight-regular;

    @include media('>=mobile') {
        margin-left: 0;
    }
}

.c-btn--download-icon .o-svg {
    stroke: color(accent);
}

.c-btn__icon {
    width: offset(main) * 3.6;
    height: offset(main) * 2.6;

    position: absolute;
    top: 0;
    bottom: offset(main);
    right: 0;
    margin: auto 0;

    @include media('>=mobile') {
        bottom: 0;
    }
}

.c-btn__icon .o-svg {
    width: 100%;
    height: 100%;
}

.c-btn--padded {
    padding: {
        left: offset(main) * 6;
        right: offset(main) * 6
    }
}

.c-btn-wrapper--equal-btns {
    justify-content: space-between;
    flex-wrap: wrap;
}

.c-btn-wrapper--equal-btns .c-btn {
    width: 100%;
    margin-bottom: offset(main);

    &:last-child {
        margin-bottom: 0;
    }

    @include media('>=mobile') {
        margin-bottom: 0;
    }
}

.c-btn-wrapper--two-btns .c-btn {
    @include media('>=mobile') {
        width: calc(50% - #{offset(main) * 1.2});
    }
}

.c-btn-wrapper--three-btns .c-btn {
    @include media('>=mobile') {
        width: calc(33% - #{offset(main) * 1.2});
    }
}

.c-feature-panel {
    width: 100%;

    @include media('>=mobile') {
        width: 50%;
        height: 100%;

        display: flex;
        flex-direction: column;
        justify-content: flex-start;
    }
    @include media('>=desktop-small') {
        width: 40%;
    }
}

.c-feature-panel__title,
.c-feature-panel__content {
    margin: 0;
}

.c-feature-panel__title {
    padding-top: offset(main) * 3;
    padding-bottom: offset(main) * 2;

    @include media('>=mobile') {
        padding-top: 0;
        padding-bottom: offset(main) * 3.5;
    }
}

.c-feature-panel .c-btn-wrapper {
    margin-top: auto;
    padding-top: offset(main) * 7;

    @include media('>=mobile') {
        padding-top: 0;
    }
}

.c-quick-links-wrapper {
    width: 100%;
    height: auto;

    background-size: cover;
    background-position: 50% 50%;
}

.c-quick-links-wrapper__slider {
    display: flex;
    flex-direction: row;
    align-items: center;
    justify-content: center;

    width: 100%;
    height: auto;

    @include media('>=mobile') {
        padding: 0 offset(main) * 3.5;
    }
    @include media('>=desktop-small') {
        padding: 0;
    }
}

.c-quick-links-wrapper__next,
.c-quick-links-wrapper__prev {
    position: absolute;

    @include arrow-small;
    cursor: pointer;

    z-index: $size-quick-links-arrow-z-index;

    outline: none;

    top: offset(main) * 11;

    @include media('>=mobile') {
        top: offset(main) * 14;
    }
    @include media('>=desktop-small') {
        display: none;
    }
}

.c-quick-links-wrapper__prev {
    transform: rotate(135deg);

    left: offset(main) * 0.5;
}

.c-quick-links-wrapper__next {
    transform: rotate(-45deg);

    right: offset(main) * 0.5;

}

.c-standfirst {

    @include media('>=desktop-small') {
        padding-top: offset(main) * 3;
    }
}

.c-standfirst-desc {
    padding-top: offset(main) * 6;
}

.c-standfirst-desc--small-padding {
    padding-top: offset(main) * 4.5;

    @include media('>=mobile') {
        padding-top: offset(main) * 3;
    }
}

.c-content-tabs {
    display: flex;
    align-items: center;
    justify-content: space-between;

    list-style: none;

    padding: 0;
    margin: 0;

    margin-top: offset(main) * 4;
}

.c-content-tabs__tab {
    width: 100%;
    height: $size-content-tabs-tab-height;

    outline: 0;

    display: flex;

    &:not(:last-child) {
        margin-right: 10px;
    }
}

.c-content-tabs__tab:hover {
    cursor: pointer;
}

.c-content-tabs__btn {
    width: 100%;
    height: 100%;

    border: none;

    display: flex;
    align-items: center;
    justify-content: center;

    background: transparent;
    color: color(accent);

    cursor: pointer;

    border: 2px solid color(accent);
}

.c-content-tabs__btn:focus {
    outline: none !important;
}

.c-content-tabs__tab.is-active .c-content-tabs__btn {
    background-color: color(accent);
    color: color(white);

    position: relative;

    &:after {
        content: '';
        display: block;

        position: absolute;
        bottom: -$size-content-tabs-tab-arrow-height;
        left: calc(50% - #{$size-content-tabs-tab-arrow-width});

        width: 0;
        height: 0;
        border-style: solid;
        border-width: $size-content-tabs-tab-arrow-height $size-content-tabs-tab-arrow-width 0 $size-content-tabs-tab-arrow-width;
        border-color: color(accent) transparent transparent transparent;
    }
}

.c-text-component {
    display: flex;
    flex-direction: column;

    padding-top: offset(main) * 3.5;
    padding-bottom: offset(main);

    @include media('>=desktop-small') {
        flex-direction: row;
        justify-content: space-between;
        flex-wrap: nowrap;

        padding-top: offset(main);
    }
}

.c-text-component--clear,
.c-text-component--clear .c-text-component__title {
    padding-top: 0;
    padding-bottom: 0;
}

.c-text-component__column {
    width: 100%;
    height: auto;
}

.c-text-component__column:nth-child(1) {

    @include media('>=desktop-small') {
        width: 33%;
    }
}

.c-text-component__column:nth-child(2) {

    @include media('>=desktop-small') {
        width: 63%;

        margin-right: - offset(main) * 4;
    }
}

.c-text-component__title,
.c-text-component__subtitle,
.c-text-component__description {
    margin: 0;
    padding: 0;
}

.c-text-component__title {
    padding-bottom: offset(main) * 1.5;
}

.c-text-component__subtitle {

    @include media('>=mobile') {
        max-width: 70%;
    }
    @include media('>=desktop-small') {
        max-width: 100%;
    }
}

.c-text-component__description {

    @include media('>=mobile') {
        max-width: 70%;
    }
    @include media('>=desktop-small') {
        max-width: 95%;
    }
}

.c-contacts {
    display: flex;
    flex-direction: column;
    align-items: center;

    @include media('>=mobile') {
        margin: 0 auto;

        max-width: 70%;
    }
    @include media('>=mobile') {
        max-width: 50%;
    }
}

.c-contacts__title {
    padding-top: offset(main) * 2;
    padding-bottom: offset(main) * 3;

    line-height: 1.5em;
}

.c-contacts__content {
    margin: 0;
    padding-bottom: offset(main) * 3;

    a {
        font-weight: 400;
    }
}

.c-dropdown {
    width: 100%;
    max-height: $size-dropdown-height;

    margin-bottom: offset(main) * 2;
    font-size: font-size(small);

    // overflow: hidden;
    transition: $transition-default;

    position: relative;

    cursor: pointer;

    @include media('>=mobile') {
        margin-bottom: offset(main) * .8;
    }
}

// .c-dropdown.is-open {
//     max-height: 500px;
// }

.c-dropdown__title {
    height: $size-dropdown-height;
    width: 100%;

    background-color: color(accent);

    border: none;
    outline: none;

    display: flex;
    align-items: center;
    justify-content: flex-start;

    padding: 0 offset(main) * 2;

    position: relative;

    cursor: pointer;

    &:after {
        content: '';

        width: offset(main) * 1.6;
        height: 100%;

        position: absolute;
        top: 0;
        right: offset(main) * 2;

        background-image: url('../img/chevron.svg');
        background-size: contain;
        background-position: 50% 50%;
        background-repeat: no-repeat;

        transition: $transition-default;
    }
}

.c-dropdown.is-open .c-dropdown__title {

    &:after {
        transform: rotate(180deg);
    }
}

.c-dropdown__options {
    list-style: none;
    margin: 0;
    padding: 0;

    margin-top: - offset(main);

    max-height: 0;
    overflow: hidden;

    transition: $transition-default;

    background-color: color(white);
}

.c-dropdown.is-open .c-dropdown__options {
    max-height: 500px;
}

.c-dropdown__option {
    width: 100%;
    height: $size-dropdown-height;
    line-height: $size-dropdown-height;

    margin-bottom: 2px;

    padding: 0 offset(main) * 2;

    background-color: color(lblue);
}

.c-dropdown--small {
    width: auto;
    margin-right: offset(main) * 2.5;
}

.c-dropdown--small .c-dropdown__title {
    padding-right: 150px;
}

.c-dropdown--small .c-dropdown__option.is-active .c-label {
    font-weight: $font-weight-bold;
}

.c-dropdown--absolute.is-open {
    max-height: 500px;
}

.c-lead-teaser-wrapper {
    width: 100%;
    height: auto;

    background-size: cover;
    background-position: 60% 50%;

    position: relative;

    @include media('>=mobile') {
        width: 100%;
        background-position: 90% 50%;
    }

    &:before {
        content: '';

        position: absolute;

        width: 100%;
        height: 100%;

        background-color: rgba(color(black), .25);
    }
}

.c-file-list {
    list-style-type: none;
    padding: 0;
    margin: 0;
}

.c-file-list__date {
    margin-bottom: 0;
    white-space: nowrap;

    color: color(lgrey);

    width: offset(main) * 18;
}

.c-file-list__item {
    border-bottom: 1px solid color(lblue);
    margin-bottom: offset(main) * 2;
    padding: offset(main) * 1.2 0;

    &:last-child {
        margin-bottom: 0;
    }

    @include media('>=mobile') {
        margin-bottom: 0;
    }
}

.c-file-list__item.is-hidden {
    display: none;
}

.c-file-list__item:hover > .c-file-list__item-wrapper {
    background-color: rgba(color(lblue), .4);
}

.c-file-list__title {
    text-align: left;
    width: 100%;

    font-weight: $font-weight-bold;

    margin-bottom: offset(main) * 1.5;

    @include media('>=mobile') {
        margin-left: offset(main) * 3;
        margin-bottom: 0;
    }
}

.c-file-list__item-wrapper {
    display: flex;
    flex-direction: column;

    transition: $transition-default;

    @include media('>=mobile') {
        align-items: center;
        flex-direction: row;
    }
}

.c-file-list__item-wrapper .c-btn-wrapper .c-btn {
    transition: $transition-default;

    padding: offset(main) offset(main) * 6 offset(main) offset(main) * 3;
}

.c-file-list__item-wrapper .c-btn-wrapper .c-btn:hover {
    background-color: color(lblue);
}

.c-file-list__item-wrapper .c-btn-wrapper .c-btn__icon {
    right: offset(main);
}

@include media('<mobile') {
    .c-file-list__item-wrapper .c-btn-wrapper .c-btn {
        padding: offset(main) / 2 offset(main) * 5;
    }

    .c-file-list__item-wrapper .c-btn-wrapper .c-btn__icon {
        left: 0;
    }
}

.c-file-list--no-mobile-margin .c-file-list__item {
    margin-bottom: 0;
}

.c-file-list--two-columns {
    display: flex;
    flex-direction: column;

    @include media('>=mobile') {
        flex-direction: row;
        flex-wrap: wrap;
        justify-content: space-between;
    }
}

.c-file-list--two-columns .c-file-list__item {
    display: flex;
    align-items: center;

    width: 100%;

    @include media('>=mobile') {
        width: 48%;

        padding: offset(l) 0;
    }
}

.c-file-list--two-columns .c-file-list__date {
    width: offset(main) * 14;

    white-space: normal;
}

.c-file-list--two-columns .c-file-list__title {
    width: auto;

    white-space: nowrap;
}

.c-file-list--no-breake-text .c-file-list__date {
    max-width: offset(main) * 9;
    width: 100%;

    white-space: normal;
}

.c-file-list--no-breake-text .c-file-list__title {
    white-space: normal;

    @include media('>=mobile') {
        margin-left: offset(main);
    }
}

.c-file-list--no-breake-text .c-btn-wrapper {
    width: 100%;

    @include media('>=mobile') {
        width: 70%;
    }
}

.c-contact-list {
    list-style-type: none;
    padding: 0;
    margin: 0;
}

.c-contact-list__text {
    margin-bottom: 0;
    white-space: nowrap;

    @include media('>=mobile') {
        min-width: $size-contact-list-text-width;
    }
}

.c-contact-list__item {
    border-bottom: 1px solid color(lblue);
    margin-bottom: offset(main) * 2;
    padding: offset(xl) / 2.1 0;

    &:first-child {
        display: none;

        @include media('>=mobile') {
           display: block;
        }
    }

    &:last-child {
        margin-bottom: 0;
    }

    @include media('>=mobile') {
        margin-bottom: 0;
    }
}

.c-contact-list__item:hover > .c-contact-list__item-wrapper {
    background-color: rgba(color(lblue), .4);
}

.c-contact-list__name {
    text-align: left;
    width: 100%;

    font-weight: $font-weight-bold;

    margin-bottom: offset(main) * 1.5;

    @include media('>=mobile') {
        margin-left: offset(main) * 8;
        margin-bottom: 0;
    }
}

.c-contact-list__item-wrapper {
    display: flex;
    flex-direction: column;

    transition: $transition-default;

    @include media('>=mobile') {
        align-items: center;
        flex-direction: row;
    }
}

.c-contact-list__item-wrapper .c-btn-wrapper .c-btn {
    transition: $transition-default;

    padding: offset(main) offset(main) * 6 offset(main) offset(main) * 3;
}

.c-contact-list__item-wrapper .c-btn-wrapper .c-btn:hover {
    background-color: color(lblue);
}

.c-contact-list__item-wrapper .c-btn-wrapper .c-btn__icon {
    right: offset(main);
}

@include media('<mobile') {
    .c-contact-list__item-wrapper .c-btn-wrapper .c-btn {
        padding: offset(main) / 2 offset(main) * 5;
    }

    .c-contact-list__item-wrapper .c-btn-wrapper .c-btn__icon {
        left: 0;
    }
}

.c-data-slider-wrapper {
    width: 100%;
    height: auto;

    background-size: cover;
    background-position: 50% 50%;
}

.c-data-slider-wrapper__slider {
    display: flex;
    flex-direction: row;
    align-items: center;
    justify-content: center;

    width: 100%;
    height: auto;

    @include media('>=mobile') {
        padding: {
            left: offset(main) * 10;
            right: offset(main) * 10;
        }
    }
    @include media('>=desktop-small') {
        padding: {
            left: offset(main) * 6;
            right: offset(main) * 6;
        }
    }
}

.c-data-slider-wrapper__next,
.c-data-slider-wrapper__prev {
    position: absolute;

    @include arrow-small;
    cursor: pointer;

    z-index: $size-quick-links-arrow-z-index;

    outline: none;

    top: offset(main) * 6;

    @include media('>=mobile') {
        @include arrow;
    }
    @include media('>=desktop-small') {
        @include arrow-big;
    }
}

.c-data-slider-wrapper__prev {
    transform: rotate(135deg);

    left: offset(main) * 0.5;

    @include media('>=desktop-small') {
        left: offset(main);
    }
}

.c-data-slider-wrapper__next {
    transform: rotate(-45deg);

    right: offset(main) * 0.5;

    @include media('>=desktop-small') {
        right: offset(main);
    }
}

.c-grid-wrapper {
    display: flex;
    align-items: stretch;
    justify-content: space-between;
    flex-wrap: wrap;

    width: 100%;
    height: auto;
}

.c-grid-wrapper__block {
    height: 100%;
}

.c-grid-block {
    display: flex;
    flex-direction: column;
    align-items: flex-start;

    width: 100%;
    height: $size-grid-component-card-height;

    margin-bottom: offset(main) * 6;

    transition: $transition-default;

    @include media('>=mobile') {
        padding: 0;
    }
    @include media('>=desktop-small') {
        opacity: 1;
    }
}

.c-grid-block--no-offset {

    @include media('>=mobile') {
        margin-bottom: 0;
    }
}

.c-grid-block.swiper-slide-active,
.c-grid-block.swiper-slide-next {
    opacity: 1;
}

.c-grid-block__image {
    width: 100%;
    height: $size-grid-component-card-img-height-by-4;

    overflow: hidden;

    position: relative;

    margin: 0;
}

.c-grid-block__image img {
    width: 100%;
    height: auto;

    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
}

.c-grid-block__title {
    padding-top: offset(main);
}

.c-grid-block__link {
    margin-top: auto;
    margin-bottom: offset(main);
    padding-top: offset(main);
}

.c-grid-block__desc {
    padding-top: offset(main) * 1.5;
    margin-bottom: 0;
}

.c-grid-block__hidden-desc {
    display: none;
}

.c-grid-wrapper {

    &--by-one {
        .c-grid-block {
            width: 100%;

            &__image {

                @include media('>=desktop-small') {
                    height: $size-grid-component-card-img-height-by-3;
                }
            }
        }
    }
    &--by-two {
        .c-grid-block {

            @include media('>=mobile') {
                width: calc(50% - #{offset(main) * 2.5 / 2});
            }

            &__image {
                @include media('>=desktop-small') {
                    height: $size-grid-component-card-img-height-by-3;
                }
            }
        }
    }
    &--by-three {
        .c-grid-block {

            @include media('>=mobile') {
                width: calc(33% - #{offset(main) * 2.5 / 2});
            }

            &__image {
                @include media('>=desktop-small') {
                    height: $size-grid-component-card-img-height-by-3;
                }
            }
        }
    }
    &--by-four {
        .c-grid-block {

            @include media('>=mobile') {
                width: calc(25% - #{offset(main) * 2.5 / 2});
            }

            &__image {
                @include media('>=desktop-small') {
                    height: $size-grid-component-card-img-height-by-4;
                }
            }
        }
    }
    &--by-five {
        .c-grid-block {

            @include media('>=mobile') {
                width: calc(20% - #{offset(main) * 2.5 / 2});
            }

            &__image {
                @include media('>=desktop-small') {
                    height: $size-grid-component-card-img-height-by-4;
                }
            }
        }
    }
}

.c-grid-block--gray-image .c-grid-block__image {
    filter: grayscale(100%);
}

.c-grid-block--link-space {
    padding-bottom: offset(main) * 5;
}

.c-list-wrapper {
    margin-bottom: offset(main) * 6;

    &:last-child {
        margin-bottom: 0;
    }
}

.c-list-wrapper__title {
    margin-bottom: offset(main) * 3;

    @include media('>=mobile') {
        margin-bottom: offset(main) * 5;
    }
}

.c-list-wrapper__filters {
    padding-bottom: offset(main) * 3;
}

.c-list-wrapper__active-filters {
    padding-bottom: offset(main) * 3;
}

.c-list-wrapper__subtitle {
    margin-bottom: offset(main) * 0.8;
}

.c-list-wrapper__link {
    margin-top: offset(main) * 2;

    @include media('>=mobile') {
        margin-top: offset(main) * 4;
    }
}

.c-list-wrapper__link button {
    outline: none;
    border: none;
    background: none;
    cursor: pointer;
}

.c-list-wrapper__link a {
    margin-bottom: 0;
}

.c-list-wrapper--offset-top {
    padding-top: offset(main) * 3;
}

.c-table-list {
    list-style-type: none;
    margin: 0;
    padding: 0;
}

.c-table-list__item {
    padding: offset(main) * 2.5 0;
    border-bottom: 1px solid color(main);
}

.c-table-list__item:last-child {
    border-bottom: 2px solid color(main);
}

.c-table-list__item--heading {
    border-width: 2px;
    padding-bottom: 0;
    padding-top: 0;
}

.c-image-group {
    list-style-type: none;
    margin: 0;
    padding: 0;
    display: flex;
    flex-wrap: wrap;
    justify-content: space-between;
}

.c-image-group__item {
    margin: offset(main) * 0.5 0;
    width: 100%;

    @include media('>=mobile') {
        width: calc(50% - #{offset(main) * 0.5});
    }
}

.c-image-group__image {
    margin: 0;
    padding: 0;
}

.c-image-group__image img {
    width: 100%;
}

.c-list-block {
    display: flex;
    flex-wrap: wrap;

    @include media('>=mobile') {
        flex-wrap: nowrap;
    }
}

.c-list-block__item {
    &:first-child {
        width: 30%;
    }

    &:last-child {
        width: 70%;
        padding-left: offset(main) * 2;
    }

    @include media('>=mobile') {
        width: auto;

        &:first-child {
            max-width: offset(main) * 20;
        }

        &:last-child {
            width: 100%;
        }
    }
}

.c-list-block__item .c-wysiwyg,
.c-list-block__item .c-wysiwyg p {
    margin: 0;
    padding: 0;
}

.c-image-content {
    display: flex;
    justify-content: space-between;
    flex-wrap: wrap;
}

.c-image-content__image {
    margin: 0;
    padding: 0;
    width: 100%;
    margin-bottom: offset(main) * 2;

    @include media('>=mobile') {
        width: 50%;
        margin-bottom: 0;
    }
}

.c-image-content__image img {
    width: 100%;
    display: block;
}

.c-image-content__wrapper {
    width: 100%;
    position: relative;

    @include media('>=mobile') {
        width: 50%;
        padding-left: offset(main) * 2.5;
    }
}

.c-image-content__wrapper .c-btn-wrapper {
    margin-top: offset(main);

    @include media('>=mobile') {
        margin-top: 0;
        position: absolute;
        bottom: 0;
    }
}

.c-image-content__text {
    margin: {
        top: offset(main) * 3;
        bottom: offset(main) * 2;
    }

    @include media('>=mobile') {
        max-width: offset(main) * 31.6;
    }
}

.c-standfirst-wrapper {
    padding-top: offset(main) * 3.7;
}

.c-standfirst-wrapper .c-standfirst {
    padding-top: 0;
}

.c-standfirst-wrapper .c-btn-wrapper {
    margin-top: offset(main) * 5.7;

    @include media('>=mobile') {
        margin-top: offset(main) * 3;
    }
}

.c-info-panel {
    display: flex;
    flex-wrap: wrap;
    position: relative;
    padding: {
        top: offset(main) * 5;
        bottom: offset(main) * 3.6;
    }

    @include media('>=mobile') {
        padding-bottom: offset(main) * 8.5;
    }
}

.c-info-panel__item {
    display: flex;
    flex-direction: column;
    align-items: center;
    width: 100%;
    margin-top: offset(main) * 8.5;

    &:first-child {
        margin-top: 0;
    }

    @include media('>=mobile') {
        width: 50%;
        margin-top: 0;
    }
}

.c-info-panel__heading {
    padding-bottom: offset(main) * 3;
}

.c-info-panel__desc {
    margin: {
        top: offset(main) * 1.5;
        bottom: offset(main) * 7;
    }
}

.c-info-panel .c-btn-wrapper {
    @include media('>=mobile') {
        position: absolute;
        bottom: offset(main) * 4;
    }
}

.c-logos-slider-wrapper {
    width: 100%;
    height: auto;

    background-size: cover;
    background-position: 50% 50%;
}

.c-logos-slider-wrapper__slider {
    display: flex;
    flex-direction: row;
    align-items: center;
    justify-content: center;

    width: 100%;
    height: auto;

    padding: {
        left: offset(main) * 3;
        right: offset(main) * 3;
    }

    @include media('>=mobile') {
        padding: {
            left: offset(main) * 10;
            right: offset(main) * 10;
        }
    }
    @include media('>=desktop-small') {
        padding: {
            left: offset(main) * 5;
            right: offset(main) * 5;
        }
    }
}

.c-logos-slider-wrapper__next,
.c-logos-slider-wrapper__prev {
    position: absolute;

    @include arrow-small;
    cursor: pointer;

    z-index: $size-quick-links-arrow-z-index;

    outline: none;

    top: 40%;

    @include media('>=mobile') {
        @include arrow;
    }
    @include media('>=desktop-small') {
        @include arrow-big;
    }
}

.c-logos-slider-wrapper__prev {
    transform: rotate(135deg);

    left: offset(main) * 1.5;

    @include media('>=desktop-small') {
        left: offset(main);
    }
}

.c-logos-slider-wrapper__next {
    transform: rotate(-45deg);

    right: offset(main) * 1.5;

    @include media('>=desktop-small') {
        right: offset(main);
    }
}
